'use strict';

module.exports.testDBInputFormat = function (cb) {

  try {
    require('../DBInputFormatTests').testDBInputFormat(require('hadoop-input').DBInputFormat, cb);
  } catch (err) {
    if (err.stack) {
      console.error(err.stack);
    }
    throw new Error(err.message);
  }

  cb();

};