'use strict';

module.exports.testTextInputFormat = function (cb) {

  try {
    require('../FileInputFormatTests').testFileInputFormat(require('hadoop-input').TextInputFormat, cb);
  } catch (err) {
    if (err.stack) {
      console.error(err.stack);
    }
    throw new Error(err.message);
  }

  cb();

};