'use strict';

module.exports.testSequenceFileOutputFormat = function (cb) {

  try {
    require('../SequenceFileOutputFormatTests').testFileOutputFormat(require('hadoop-output').SequenceFileOutputFormat);
  } catch (err) {
    if (err.stack) {
      console.error(err.stack);
    }
    throw new Error(err.message);
  }

  cb();

};