'use strict';

// Use a submodule to make sure things work right
var submodule = require('./submodule');

module.exports = {
  config: {},

  map: function (key, value, context, cb) {
    // Retrieve your IP address and emit it to show we can successfully use a Node.js module

    submodule.getIp(function (res) {
      if (res instanceof Error) {
        cb(err);
      } else {
        context.write('ip', res);
        cb();
      }
    });
  }
};
