// Broken up into a submodule to make sure things work properly

'use strict';

var http = require('http');

module.exports.getIp = function (cb) {
  var req = http.request({
    hostname: 'jsonip.com'
  }, function(res) {
    var ipAddr = '';

    res.setEncoding('utf8');

    // Make sure we get all the data from the response
    res.on('data', function (chunk) {
      ipAddr += chunk;
    });

    // Response has finished so callback, null is because we  should
    // indicate there was no error
    res.on('end', function() {
      cb(JSON.parse(ipAddr).ip);
    });

    res.on('error', function (err) {
      cb(err);
    });
  });

  // We must say we have finished the request
  req.end();
};
