'use strict';

module.exports = {
  config: {},

  map: function (key, value, context, cb) {
    // This is just required for the test but it actually isn't used

    // Here just in case the Error isn't thrown above
    cb();
  },

  sort: function (key1, key2, cb) {
    var sort;

    if (key1 === key2) {
      sort = 0;
    } else if (key1 < key2) {
      sort = -1;
    } else {
      sort = 1;
    }

    cb(sort);
  }
};
